import React from 'react';
import { Form, Card, FloatingLabel, FormLabel, Container } from 'react-bootstrap';

// Маленькая форма для преподавателя
const CoordinatorForm = ({ onChange, coordinator }) => {
    const handleChange = (e) => {
        const updatedData = coordinator;
        onChange(updatedData);
    };

    return (
        <Container>
            <Card style={{ width: '100%', minHeight: '300px', marginBottom: '10px', position: 'relative' }}>
                <Card.Body>
                    <Form>
                        <FormLabel className='mb-3'></FormLabel>
                        <FloatingLabel
                            controlId="formTeacherFirstName"
                            label="Фамилия"
                            className="mb-2">
                            <Form.Control
                                type="text"
                                name="f_name"
                                value={coordinator.name}
                                onChange={handleChange} />
                        </FloatingLabel>

                        <FloatingLabel
                            controlId="formTeacherMiddleName"
                            label="Имя"
                            className="mb-2">
                            <Form.Control
                                type="text"
                                name="m_name"
                                value={coordinator.phone}
                                onChange={handleChange} />
                        </FloatingLabel>

                        <FloatingLabel
                            controlId="formTeacherLastName"
                            label="Отчество"
                            className="mb-2">
                            <Form.Control
                                type="text"
                                name="l_name"
                                value={coordinator.address}
                                onChange={handleChange} />
                        </FloatingLabel>

                        <FloatingLabel
                            controlId="formTeacherPosition"
                            label="Должность"
                            className="mb-2">
                            <Form.Control
                                type="text"
                                name="position"
                                value={coordinator.email}
                                onChange={handleChange} />
                        </FloatingLabel>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
};

export default CoordinatorForm;
