import React, { useState } from 'react';
import { Form, Button, Col, Row, Container, Card } from 'react-bootstrap';

// Маленькая форма для преподавателя
const TeacherForm = ({ onChange, teacherData, onRemove }) => {
    const handleChange = (e) => {
        const { name, value } = e.target;
        const updatedData = { ...teacherData, [name]: value };
        onChange(updatedData);
    };

    return (
        <Card style={{ width: '26rem', marginBottom: '10px' }}>
            <Card.Body>
                <Form>
                    <Form.Group as={Row} controlId="formTeacherPosition">
                        <Form.Label column sm={4}>Должность:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="position"
                                value={teacherData.position}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formTeacherFirstName">
                        <Form.Label column sm={4}>Фамилия:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="f_name"
                                value={teacherData.f_name}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formTeacherMiddleName">
                        <Form.Label column sm={4}>Имя:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="m_name"
                                value={teacherData.m_name}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formTeacherLastName">
                        <Form.Label column sm={4}>Отчество:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="l_name"
                                value={teacherData.l_name}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formTeacherWorkExperience">
                        <Form.Label column sm={4}>Опыт работы:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="workExperience"
                                value={teacherData.workExperience}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formTeacherWorkplace">
                        <Form.Label column sm={4}>Место работы:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="workplace"
                                value={teacherData.workplace}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Button variant="danger" onClick={onRemove}>Удалить</Button>
                </Form>
            </Card.Body>
        </Card>
    );
};

// Основная форма
const PrimaryForm = () => {
    const [formData, setFormData] = useState({
        programName: '',
        programTypeID: '',
        programDescriptionShort: '',
        programDescription: '',
        targetAudience: '',
        programHours: '',
        lessonSchedule: '',
        studyPeriod: '',
        educationCost: '',
        ksuDepartmentID: '',
        typeGraduationDocID: '',
        programCoordinatorID: '',
        teacherID: '',
        accountID: '', // Это будет ID текущего пользователя
    });

    const [teachers, setTeachers] = useState([
        { position: '', f_name: '', m_name: '', l_name: '', workExperience: '', workplace: '' },
    ]);

    const currentUser = { id: 1 }; // Пример ID текущего пользователя

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleTeacherChange = (index, teacherData) => {
        const updatedTeachers = [...teachers];
        updatedTeachers[index] = teacherData;
        setTeachers(updatedTeachers);
    };

    const handleAddTeacher = () => {
        if (teachers.length < 6) {
            setTeachers([
                ...teachers,
                { position: '', f_name: '', m_name: '', l_name: '', workExperience: '', workplace: '' },
            ]);
        }
    };

    const handleRemoveTeacher = (index) => {
        if (teachers.length > 1) {
            const updatedTeachers = teachers.filter((_, i) => i !== index);
            setTeachers(updatedTeachers);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const dataToSend = {
            ...formData,
            teachers,
            accountID: currentUser.id, // Привязываем текущий аккаунт
        };

        console.log('Отправленные данные:', dataToSend);
        // Здесь вы можете отправить данные на сервер
    };

    return (
        <Container>
            <Form onSubmit={handleSubmit}>
                <h2>Создание Первичной Формы</h2>

                {/* Поля формы программы */}
                <Form.Group as={Row} controlId="formProgramName">
                    <Form.Label column sm={2}>Наименование программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programName"
                            value={formData.programName}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                {/* Прочие поля формы */}
                {/* ... другие поля формы */}

                <div>
                    <h3>Преподаватели</h3>
                    <Row>
                        {teachers.map((teacher, index) => (
                            <Col key={index} sm={4} className="mb-4">
                                <TeacherForm
                                    teacherData={teacher}
                                    onChange={(teacherData) => handleTeacherChange(index, teacherData)}
                                    onRemove={() => handleRemoveTeacher(index)}
                                />
                            </Col>
                        ))}
                    </Row>

                    {/* Карточка для добавления преподавателя */}
                    {teachers.length < 6 && (
                        <Card style={{ width: '26rem', marginBottom: '10px' }}>
                            <Card.Body>
                                <Button variant="success" onClick={handleAddTeacher}>
                                    + Добавить преподавателя
                                </Button>
                            </Card.Body>
                        </Card>
                    )}
                </div>

                <Button variant="primary" type="submit">
                    Отправить
                </Button>
            </Form>
        </Container>
    );
};

export default PrimaryForm;
