import React from 'react';
import { Row, Col, Card, Container, Button } from 'react-bootstrap';
import TeacherForm from './TeacherForm';

const TeachersGroup = ({ teachers, onTeacherChange, onAddTeacher, onRemoveTeacher }) => {
    return (
        <Container>
            <h3>Преподаватели</h3>
            <Row className="d-flex flex-wrap">
                {teachers.map((teacher, index) => (
                    <Col key={index} xs={12} md={4} lg={4} className="mb-4 position-relative">
                        <Card
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                width: '100%',
                                minHeight: '450px',
                                border: '2px solid #ccc',
                            }}
                        >
                            <Button
                                variant="link"
                                className="close-button"
                                style={{
                                    position: 'absolute',
                                    top: '10px',
                                    right: '10px',
                                    fontSize: '20px',
                                    color: '#888',
                                    background: 'transparent',
                                    border: 'none',
                                    cursor: 'pointer',
                                }}
                                onClick={() => onRemoveTeacher(index)}
                            >
                                &times;
                            </Button>
                            <TeacherForm
                                teacherData={teacher}
                                onChange={(data) => onTeacherChange(index, data)}
                            />
                        </Card>
                    </Col>
                ))}
                {teachers.length < 6 && (
                    <Col xs={12} md={4} lg={4} className="mb-4">
                        <Card
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                width: '100%',
                                minHeight: '450px',
                                border: '2px dashed #ccc',
                                cursor: 'pointer',
                            }}
                            onClick={onAddTeacher}
                        >
                            <Card.Body className="d-flex align-items-center justify-content-center">
                                <h5>+ Добавить преподавателя</h5>
                            </Card.Body>
                        </Card>
                    </Col>
                )}
            </Row>
        </Container>
    );
};

export default TeachersGroup;
