import React from 'react';
import { Row, Col, Card } from 'react-bootstrap';
import AscpectField from './AspectField';

const AspectGroup = ({ aspects, onAspectChange, onAddAspect, onRemoveAspect }) => {
    return (
        <div>
            <h3>Аспекты</h3>
            <Row className="d-flex flex-wrap">
                {aspects.map((aspect, index) => (
                    <Col key={index} xs={12} sm={6} md={4} lg={'auto'} className="mb-4">
                        <AscpectField
                            aspectData={aspect}
                            onChange={(data) => onAspectChange(index, data)}
                            onRemove={() => onRemoveAspect(index)}
                        />
                    </Col>
                ))}
                {aspects.length < 6 && (
                    <Col xs={12} sm={6} md={4} lg={'auto'} className="mb-4">
                        <Card
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                width: '415px',
                                minHeight: '125px',
                                border: '2px dashed #ccc',
                                cursor: 'pointer',
                            }}
                            onClick={onAddAspect}
                        >
                            <Card.Body className="d-flex align-items-center justify-content-center">
                                <h5>+ Добавить преподавателя</h5>
                            </Card.Body>
                        </Card>
                    </Col>
                )}
            </Row>
        </div>
    );
};

export default AspectGroup;
