import React from 'react';
import { Row, Col, Card } from 'react-bootstrap';
import TeacherForm from './TeacherForm';

const TeachersForm = ({ teachers, onTeacherChange, onAddTeacher, onRemoveTeacher }) => {
    return (
        <div>
            <h3>Преподаватели</h3>
            <Row className="d-flex flex-wrap">
                {teachers.map((teacher, index) => (
                    <Col key={index} xs={12} sm={6} md={4} lg={'auto'} className="mb-4">
                        <TeacherForm
                            teacherData={teacher}
                            onChange={(data) => onTeacherChange(index, data)}
                            onRemove={() => onRemoveTeacher(index)}
                        />
                    </Col>
                ))}
                {teachers.length < 6 && (
                    <Col xs={12} sm={6} md={4} lg={'auto'} className="mb-4">
                        <Card
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                width: '415px',
                                minHeight: '450px',
                                border: '2px dashed #ccc',
                                cursor: 'pointer',
                            }}
                            onClick={onAddTeacher}
                        >
                            <Card.Body className="d-flex align-items-center justify-content-center">
                                <h5>+ Добавить преподавателя</h5>
                            </Card.Body>
                        </Card>
                    </Col>
                )}
            </Row>
        </div>
    );
};

export default TeachersForm;
