import React, { useState } from 'react';
import { Form, Button, Col, Row, Container, Card, ListGroup } from 'react-bootstrap';

// Маленькая форма для преподавателя
const TeacherForm = ({ onChange, teacherData, onRemove }) => {
    const handleChange = (e) => {
        const { name, value } = e.target;
        const updatedData = { ...teacherData, [name]: value };
        onChange(updatedData);
    };

    return (
        <Card style={{ width: '18rem', marginBottom: '10px' }}>
            <Card.Body>
                <Form>
                    <Form.Group as={Row} controlId="formTeacherPosition">
                        <Form.Label column sm={4}>Должность:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="position"
                                value={teacherData.position}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formTeacherFullName">
                        <Form.Label column sm={4}>ФИО:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="full_name"
                                value={teacherData.full_name}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formTeacherWorkExperience">
                        <Form.Label column sm={4}>Опыт работы:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="workExperience"
                                value={teacherData.workExperience}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formTeacherWorkplace">
                        <Form.Label column sm={4}>Место работы:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="workplace"
                                value={teacherData.workplace}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Button variant="danger" onClick={onRemove}>Удалить</Button>
                </Form>
            </Card.Body>
        </Card>
    );
};

// Маленькая форма для координатора программы
const ProgramCoordinatorForm = ({ onChange, coordinatorData, onRemove }) => {
    const handleChange = (e) => {
        const { name, value } = e.target;
        const updatedData = { ...coordinatorData, [name]: value };
        onChange(updatedData);
    };

    return (
        <Card style={{ width: '18rem', marginBottom: '10px' }}>
            <Card.Body>
                <Form>
                    <Form.Group as={Row} controlId="formCoordinatorPhone">
                        <Form.Label column sm={4}>Телефон:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="phone"
                                value={coordinatorData.phone}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formCoordinatorEmail">
                        <Form.Label column sm={4}>E-mail:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="email"
                                name="eMail"
                                value={coordinatorData.eMail}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formCoordinatorFullName">
                        <Form.Label column sm={4}>ФИО:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="full_name"
                                value={coordinatorData.full_name}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Form.Group as={Row} controlId="formCoordinatorAddress">
                        <Form.Label column sm={4}>Адрес:</Form.Label>
                        <Col sm={8}>
                            <Form.Control
                                type="text"
                                name="address"
                                value={coordinatorData.address}
                                onChange={handleChange}
                            />
                        </Col>
                    </Form.Group>

                    <Button variant="danger" onClick={onRemove}>Удалить</Button>
                </Form>
            </Card.Body>
        </Card>
    );
};

// Основная форма
const PrimaryForm = () => {
    const [formData, setFormData] = useState({
        programName: '',
        programTypeID: '',
        programDescriptionShort: '',
        programDescription: '',
        targetAudience: '',
        programHours: '',
        lessonSchedule: '',
        studyPeriod: '',
        educationCost: '',
        ksuDepartmentID: '',
        typeGraduationDocID: '',
        programCoordinatorID: '',
        teacherID: '',
        accountID: '', // Это будет ID текущего пользователя
    });

    const [teachers, setTeachers] = useState([
        { position: '', full_name: '', workExperience: '', workplace: '' },
    ]);

    const [coordinators, setCoordinators] = useState([
        { phone: '', eMail: '', full_name: '', address: '' },
    ]);

    const currentUser = { id: 1 }; // Пример ID текущего пользователя

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleTeacherChange = (index, teacherData) => {
        const updatedTeachers = [...teachers];
        updatedTeachers[index] = teacherData;
        setTeachers(updatedTeachers);
    };

    const handleCoordinatorChange = (index, coordinatorData) => {
        const updatedCoordinators = [...coordinators];
        updatedCoordinators[index] = coordinatorData;
        setCoordinators(updatedCoordinators);
    };

    const handleAddTeacher = () => {
        setTeachers([
            ...teachers,
            { position: '', full_name: '', workExperience: '', workplace: '' },
        ]);
    };

    const handleAddCoordinator = () => {
        setCoordinators([
            ...coordinators,
            { phone: '', eMail: '', full_name: '', address: '' },
        ]);
    };

    const handleRemoveTeacher = (index) => {
        const updatedTeachers = teachers.filter((_, i) => i !== index);
        setTeachers(updatedTeachers);
    };

    const handleRemoveCoordinator = (index) => {
        const updatedCoordinators = coordinators.filter((_, i) => i !== index);
        setCoordinators(updatedCoordinators);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const dataToSend = {
            ...formData,
            teachers,
            coordinators,
            accountID: currentUser.id, // Привязываем текущий аккаунт
        };

        console.log('Отправленные данные:', dataToSend);
        // Здесь вы можете отправить данные на сервер
    };

    return (
        <Container>
            <Form onSubmit={handleSubmit}>
                <h2>Создание Первичной Формы</h2>

                <Form.Group as={Row} controlId="formProgramName">
                    <Form.Label column sm={2}>Наименование программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programName"
                            value={formData.programName}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramType">
                    <Form.Label column sm={2}>Вид программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programTypeID"
                            value={formData.programTypeID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramDescriptionShort">
                    <Form.Label column sm={2}>Короткое описание программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programDescriptionShort"
                            value={formData.programDescriptionShort}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramDescription">
                    <Form.Label column sm={2}>Описание программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            as="textarea"
                            rows={3}
                            name="programDescription"
                            value={formData.programDescription}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formTargetAudience">
                    <Form.Label column sm={2}>Целевая аудитория:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="targetAudience"
                            value={formData.targetAudience}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>



                {/* ВМЕСТО ЭТОГО ТЕКСТА ГРУППА АСПЕКТОВ */}



                {/* ВМЕСТО ЭТОГО ТЕКСТА ГРУППА МОДУЛЕЙ */}


                <Form.Group as={Row} controlId="formProgramHours">
                    <Form.Label column sm={2}>Общее количество часов:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="number"
                            name="programHours"
                            value={formData.programHours}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formLessonSchedule">
                    <Form.Label column sm={2}>Режим занятий:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="lessonSchedule"
                            value={formData.lessonSchedule}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formStudyPeriod">
                    <Form.Label column sm={2}>Срок обучения:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="studyPeriod"
                            value={formData.studyPeriod}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formGraduationDoc">
                    <Form.Label column sm={2}>Документ по окончании:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="typeGraduationDocID"
                            value={formData.typeGraduationDocID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formEducationCost">
                    <Form.Label column sm={2}>Стоимость обучения:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="number"
                            name="educationCost"
                            value={formData.educationCost}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formDepartment">
                    <Form.Label column sm={2}>Структурное подразделение КГУ, инициирующее программу:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="ksuDepartmentID"
                            value={formData.ksuDepartmentID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <div>
                    <h3>Преподаватели</h3>
                    {teachers.map((teacher, index) => (
                        <TeacherForm
                            key={index}
                            teacherData={teacher}
                            onChange={(teacherData) => handleTeacherChange(index, teacherData)}
                            onRemove={() => handleRemoveTeacher(index)}
                        />
                    ))}
                    <Button variant="success" onClick={handleAddTeacher}>+ Добавить преподавателя</Button>
                </div>

                <div>
                    <h3>Координаторы программы</h3>
                    {coordinators.map((coordinator, index) => (
                        <ProgramCoordinatorForm
                            key={index}
                            coordinatorData={coordinator}
                            onChange={(coordinatorData) => handleCoordinatorChange(index, coordinatorData)}
                            onRemove={() => handleRemoveCoordinator(index)}
                        />
                    ))}
                    <Button variant="success" onClick={handleAddCoordinator}>+ Добавить координатора</Button>
                </div>

                <Button variant="primary" type="submit">
                    Отправить
                </Button>
            </Form>
        </Container>
    );
};

export default PrimaryForm;
