import React, { useState } from 'react';
import { Form, Button, Col, Row, Container, Card } from 'react-bootstrap';

// Маленькая форма для преподавателя
const TeacherForm = ({ onChange, teacherData }) => {
    const handleChange = (e) => {
        const { name, value } = e.target;
        const updatedData = { ...teacherData, [name]: value };
        onChange(updatedData);
    };

    return (
        <div>
            <h3>Преподаватель</h3>
            <Card style={{ width: '18rem', marginBottom: '10px' }}>
                <Card.Body>
                    <Form>
                        <Form.Group as={Row} controlId="formTeacherPosition">
                            <Form.Label column sm={4}>Должность:</Form.Label>
                            <Col sm={8}>
                                <Form.Control
                                    type="text"
                                    name="position"
                                    value={teacherData.position}
                                    onChange={handleChange}
                                />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formTeacherFullName">
                            <Form.Label column sm={4}>ФИО:</Form.Label>
                            <Col sm={8}>
                                <Form.Control
                                    type="text"
                                    name="full_name"
                                    value={teacherData.full_name}
                                    onChange={handleChange}
                                />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formTeacherWorkExperience">
                            <Form.Label column sm={4}>Опыт работы:</Form.Label>
                            <Col sm={8}>
                                <Form.Control
                                    type="text"
                                    name="workExperience"
                                    value={teacherData.workExperience}
                                    onChange={handleChange}
                                />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formTeacherWorkplace">
                            <Form.Label column sm={4}>Место работы:</Form.Label>
                            <Col sm={8}>
                                <Form.Control
                                    type="text"
                                    name="workplace"
                                    value={teacherData.workplace}
                                    onChange={handleChange}
                                />
                            </Col>
                        </Form.Group>

                        <Button variant="danger" onClick={onRemove}>Удалить</Button>
                    </Form>
                </Card.Body>
            </Card>

        </div>
    );
};

// Маленькая форма для координатора программы
const ProgramCoordinatorForm = ({ onChange, coordinatorData }) => {
    const handleChange = (e) => {
        const { name, value } = e.target;
        const updatedData = { ...coordinatorData, [name]: value };
        onChange(updatedData);
    };

    return (
        <div>
            <h3>Координатор программы</h3>
            <Card style={{ width: '18rem' }}>
                <Card.Body>
                    <Form>
                        <Form.Group as={Row} controlId="formCoordinatorPhone">
                            <Form.Label column sm={4}>Телефон:</Form.Label>
                            <Col sm={8}>
                                <Form.Control
                                    type="text"
                                    name="phone"
                                    value={coordinatorData.phone}
                                    onChange={handleChange}
                                />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formCoordinatorEmail">
                            <Form.Label column sm={4}>E-mail:</Form.Label>
                            <Col sm={8}>
                                <Form.Control
                                    type="email"
                                    name="eMail"
                                    value={coordinatorData.eMail}
                                    onChange={handleChange}
                                />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formCoordinatorFullName">
                            <Form.Label column sm={4}>ФИО:</Form.Label>
                            <Col sm={8}>
                                <Form.Control
                                    type="text"
                                    name="full_name"
                                    value={coordinatorData.full_name}
                                    onChange={handleChange}
                                />
                            </Col>
                        </Form.Group>

                        <Form.Group as={Row} controlId="formCoordinatorAddress">
                            <Form.Label column sm={4}>Адрес:</Form.Label>
                            <Col sm={8}>
                                <Form.Control
                                    type="text"
                                    name="address"
                                    value={coordinatorData.address}
                                    onChange={handleChange}
                                />
                            </Col>
                        </Form.Group>
                    </Form>
                </Card.Body>
            </Card>
        </div>
    );
};

// Основная форма
const PrimaryForm = () => {
    const [formData, setFormData] = useState({
        programName: '',
        programTypeID: '',
        programDescriptionShort: '',
        programDescription: '',
        targetAudience: '',
        programHours: '',
        lessonSchedule: '',
        studyPeriod: '',
        educationCost: '',
        ksuDepartmentID: '',
        typeGraduationDocID: '',
        programCoordinatorID: '',
        teacherID: '',
        accountID: '', // Это будет ID текущего пользователя
    });

    const [teacherData, setTeacherData] = useState({
        position: '',
        full_name: '',
        workExperience: '',
        workplace: '',
    });

    const [coordinatorData, setCoordinatorData] = useState({
        phone: '',
        eMail: '',
        full_name: '',
        address: '',
    });

    const currentUser = { id: 1 }; // Пример ID текущего пользователя

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleTeacherChange = (teacherData) => {
        setTeacherData(teacherData);
    };

    const handleCoordinatorChange = (coordinatorData) => {
        setCoordinatorData(coordinatorData);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const dataToSend = {
            ...formData,
            teacherData,
            coordinatorData,
            accountID: currentUser.id, // Привязываем текущий аккаунт
        };

        console.log('Отправленные данные:', dataToSend);
        // Здесь вы можете отправить данные на сервер
    };

    return (
        <Container>
            <Form onSubmit={handleSubmit}>
                <h2>Создание Первичной Формы</h2>

                <Form.Group as={Row} controlId="formProgramName">
                    <Form.Label column sm={2}>Наименование программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programName"
                            value={formData.programName}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramType">
                    <Form.Label column sm={2}>Вид программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programTypeID"
                            value={formData.programTypeID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramDescriptionShort">
                    <Form.Label column sm={2}>Короткое описание программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programDescriptionShort"
                            value={formData.programDescriptionShort}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramDescription">
                    <Form.Label column sm={2}>Описание программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            as="textarea"
                            rows={3}
                            name="programDescription"
                            value={formData.programDescription}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formTargetAudience">
                    <Form.Label column sm={2}>Целевая аудитория:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="targetAudience"
                            value={formData.targetAudience}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>



                {/* ВМЕСТО ЭТОГО ТЕКСТА ГРУППА АСПЕКТОВ */}



                {/* ВМЕСТО ЭТОГО ТЕКСТА ГРУППА МОДУЛЕЙ */}


                <Form.Group as={Row} controlId="formProgramHours">
                    <Form.Label column sm={2}>Общее количество часов:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="number"
                            name="programHours"
                            value={formData.programHours}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formLessonSchedule">
                    <Form.Label column sm={2}>Режим занятий:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="lessonSchedule"
                            value={formData.lessonSchedule}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formStudyPeriod">
                    <Form.Label column sm={2}>Срок обучения:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="studyPeriod"
                            value={formData.studyPeriod}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formGraduationDoc">
                    <Form.Label column sm={2}>Документ по окончании:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="typeGraduationDocID"
                            value={formData.typeGraduationDocID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formEducationCost">
                    <Form.Label column sm={2}>Стоимость обучения:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="number"
                            name="educationCost"
                            value={formData.educationCost}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formDepartment">
                    <Form.Label column sm={2}>Структурное подразделение КГУ, инициирующее программу:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="ksuDepartmentID"
                            value={formData.ksuDepartmentID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <TeacherForm teacherData={teacherData} onChange={handleTeacherChange} />
                <ProgramCoordinatorForm coordinatorData={coordinatorData} onChange={handleCoordinatorChange} />

                <Button variant="primary" type="submit">
                    Отправить
                </Button>
            </Form>
        </Container>
    );
};

export default PrimaryForm;
