import React from 'react';
import { Form, Button, Card, FloatingLabel } from 'react-bootstrap';

// Маленькая форма для аспектов
const AscpectField = ({ onChange, aspectData, onRemove }) => {
    const handleChange = (e) => {
        const { name, value } = e.target;
        const updatedData = { ...aspectData, [name]: value };
        onChange(updatedData);
    };

    return (
        <Card style={{ minWidth: '415px', minHeight: '300px', marginBottom: '10px' }}>
            <Card.Body>
                <Form>
                    <FloatingLabel
                        controlId="AscpectFieldName"
                        label="Аспект"
                        className="mb-2">
                        <Form.Control
                            type="text"
                            name="name"
                            value={aspectData.name}
                            onChange={handleChange} />
                    </FloatingLabel>
                    <Button variant="danger" onClick={onRemove}>
                        Удалить
                    </Button>
                </Form>
            </Card.Body>
        </Card>
    );
};

export default AscpectField
