import React from 'react';
import { Row, Col, Card, Container } from 'react-bootstrap';
import AscpectField from './AspectField';

const AspectGroup = ({ aspects, onAspectChange, onAddAspect, onRemoveAspect }) => {
    // Разбиваем аспекты на три группы
    const columns = [[], [], []];
    aspects.forEach((aspect, index) => {
        columns[index % 3].push(
            <AscpectField
                key={index}
                aspectData={aspect}
                onChange={(data) => onAspectChange(index, data)}
                onRemove={() => onRemoveAspect(index)}
            />
        );
    });

    return (
        <Container>
            <h3>Аспекты</h3>
            <Row>
                {columns.map((column, colIndex) => (
                    <Col key={colIndex} xs={12} md={4}>
                        {column}
                    </Col>
                ))}
                {aspects.length < 6 && (
                    <Col xs={12} md={4}>
                        <Card
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                width: '100%',
                                minHeight: '125px',
                                border: '2px dashed #ccc',
                                cursor: 'pointer',
                                marginBottom: '10px',
                            }}
                            onClick={onAddAspect}
                        >
                            <Card.Body className="d-flex align-items-center justify-content-center">
                                <h5>+ Добавить аспект</h5>
                            </Card.Body>
                        </Card>
                    </Col>
                )}
            </Row>
        </Container>
    );
};

export default AspectGroup;
