import { useState } from 'react';
import { Container, FloatingLabel } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

function LoginForm() {
    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        setValidated(true);
    };

    return (
        <Container className='container-fluid mt-5' style={{display: 'grid', justifyContent: 'center'}}>
            <Form noValidate validated={validated} onSubmit={handleSubmit}>
                <Row className="mb-3">
                    <Form.Group as={Col} md="auto" controlId="validationCustom01">
                        <FloatingLabel controlId='floatingLogin' label='Login'>
                            <Form.Control
                                required
                                type="text"
                                placeholder="Login"
                                defaultValue=""
                                isValid={validated && document.getElementById('validationCustom01')?.checkValidity()}
                                isInvalid={validated && !document.getElementById('validationCustom01')?.checkValidity()}
                            />
                        </FloatingLabel>
                        <Form.Control.Feedback type="valid">Looks good!</Form.Control.Feedback>
                        <Form.Control.Feedback type="invalid">Please provide a valid login.</Form.Control.Feedback>
                    </Form.Group>
                </Row>
                <Row className="mb-3">
                    <Form.Group as={Col} md="auto" controlId="validationCustom02">
                        <FloatingLabel controlId='floatingPassword' label='Password'>
                            <Form.Control
                                required
                                type="password"
                                placeholder='Password'
                                defaultValue=""
                                isValid={validated && document.getElementById('validationCustom02')?.checkValidity()}
                                isInvalid={validated && !document.getElementById('validationCustom02')?.checkValidity()}
                            />
                        </FloatingLabel>
                        <Form.Control.Feedback type="valid">Looks good!</Form.Control.Feedback>
                        <Form.Control.Feedback type="invalid">Please provide a valid password.</Form.Control.Feedback>
                    </Form.Group>
                </Row>
                <Form.Group className="mb-3">
                    <Form.Check
                        required
                        label="Remember me"
                        feedback="You must agree before submitting."
                        feedbackType="invalid"
                    />
                </Form.Group>
                <Button type="submit">Sign In</Button>
            </Form>
        </Container>
    );
}

export default LoginForm;
