import React, { useState } from 'react';
import { Form, Button, Col, Row } from 'react-bootstrap';

const SurveyForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    feedback: '',
    agreeToTerms: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // Отправка данных на сервер
    // axios.post('/api/form-submit', formData)
    //   .then(response => console.log('Form submitted:', response.data))
    //   .catch(error => console.error('Form submission error:', error));
  };

  return (
    <div className="container">
      <h2 className="my-4">Форма обратной связи</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group as={Row} controlId="formFullName">
          <Form.Label column sm={2}>Полное имя:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="text"
              placeholder="Введите ваше имя"
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formEmail">
          <Form.Label column sm={2}>E-mail:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="email"
              placeholder="Введите ваш e-mail"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formPhoneNumber">
          <Form.Label column sm={2}>Телефон:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="tel"
              placeholder="Введите ваш телефон"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formFeedback">
          <Form.Label column sm={2}>Ваш отзыв:</Form.Label>
          <Col sm={10}>
            <Form.Control
              as="textarea"
              rows={4}
              placeholder="Поделитесь своим мнением"
              name="feedback"
              value={formData.feedback}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formAgreeToTerms">
          <Col sm={{ span: 10, offset: 2 }}>
            <Form.Check
              type="checkbox"
              label="Я согласен с условиями использования"
              name="agreeToTerms"
              checked={formData.agreeToTerms}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Button variant="primary" type="submit">
          Отправить
        </Button>
      </Form>
    </div>
  );
};

export default SurveyForm;
