import React, { useState } from 'react';
import { Form, Button, Col, Row, Container, Card, FloatingLabel } from 'react-bootstrap';




// Основная форма
const PrimaryForm = () => {
    const [formData, setFormData] = useState({
        programName: '',
        programTypeID: '',
        programDescriptionShort: '',
        programDescription: '',
        targetAudience: '',
        programHours: '',
        lessonSchedule: '',
        studyPeriod: '',
        educationCost: '',
        ksuDepartmentID: '',
        typeGraduationDocID: '',
        programCoordinatorID: '',
        teacherID: '',
        accountID: '', // Это будет ID текущего пользователя
    });

    const [teachers, setTeachers] = useState([
        { position: '', f_name: '', m_name: '', l_name: '', workExperience: '', workplace: '' },
    ]);

    const currentUser = { id: 1 }; // Пример ID текущего пользователя

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleTeacherChange = (index, teacherData) => {
        const updatedTeachers = [...teachers];
        updatedTeachers[index] = teacherData;
        setTeachers(updatedTeachers);
    };

    const handleAddTeacher = () => {
        if (teachers.length < 6) {
            setTeachers([
                ...teachers,
                { position: '', f_name: '', m_name: '', l_name: '', workExperience: '', workplace: '' },
            ]);
        }
    };

    const handleRemoveTeacher = (index) => {
        if (teachers.length > 1) {
            const updatedTeachers = teachers.filter((_, i) => i !== index);
            setTeachers(updatedTeachers);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const dataToSend = {
            ...formData,
            teachers,
            accountID: currentUser.id, // Привязываем текущий аккаунт
        };

        console.log('Отправленные данные:', dataToSend);
        // Здесь вы можете отправить данные на сервер
    };

    return (
        <Container>
            <Form onSubmit={handleSubmit}>
                <h2>Создание Первичной Формы</h2>

                <Form.Group as={Row} controlId="formProgramName">
                    <Form.Label column sm={2}>Наименование программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programName"
                            value={formData.programName}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramType">
                    <Form.Label column sm={2}>Вид программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programTypeID"
                            value={formData.programTypeID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramDescriptionShort">
                    <Form.Label column sm={2}>Короткое описание программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="programDescriptionShort"
                            value={formData.programDescriptionShort}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramDescription">
                    <Form.Label column sm={2}>Описание программы:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            as="textarea"
                            rows={3}
                            name="programDescription"
                            value={formData.programDescription}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formTargetAudience">
                    <Form.Label column sm={2}>Целевая аудитория:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="targetAudience"
                            value={formData.targetAudience}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formProgramHours">
                    <Form.Label column sm={2}>Общее количество часов:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="number"
                            name="programHours"
                            value={formData.programHours}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formLessonSchedule">
                    <Form.Label column sm={2}>Режим занятий:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="lessonSchedule"
                            value={formData.lessonSchedule}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formStudyPeriod">
                    <Form.Label column sm={2}>Срок обучения:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="studyPeriod"
                            value={formData.studyPeriod}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formGraduationDoc">
                    <Form.Label column sm={2}>Документ по окончании:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="typeGraduationDocID"
                            value={formData.typeGraduationDocID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formEducationCost">
                    <Form.Label column sm={2}>Стоимость обучения:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="number"
                            name="educationCost"
                            value={formData.educationCost}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <Form.Group as={Row} controlId="formDepartment">
                    <Form.Label column sm={2}>Структурное подразделение КГУ, инициирующее программу:</Form.Label>
                    <Col sm={10}>
                        <Form.Control
                            type="text"
                            name="ksuDepartmentID"
                            value={formData.ksuDepartmentID}
                            onChange={handleFormChange}
                        />
                    </Col>
                </Form.Group>

                <div>
                    <h3>Преподаватели</h3>
                    <Row className="d-flex flex-wrap">
                        {teachers.map((teacher, index) => (
                            <Col key={index} xs={12} sm={6} md={4} lg={'auto'} className="mb-4">
                                <TeacherForm
                                    teacherData={teacher}
                                    onChange={(data) => handleTeacherChange(index, data)}
                                    onRemove={() => handleRemoveTeacher(index)}
                                />
                            </Col>
                        ))}
                        {teachers.length < 6 && (
                            <Col xs={12} sm={6} md={4} lg={'auto'} className="mb-4">
                                <Card
                                    className="d-flex align-items-center justify-content-center"
                                    style={{
                                        width: '415px',
                                        minHeight: '450px',
                                        border: '2px dashed #ccc',
                                        cursor: 'pointer',
                                    }}
                                    onClick={handleAddTeacher}
                                >
                                    <Card.Body className="d-flex align-items-center justify-content-center">
                                        <h5>+ Добавить преподавателя</h5>
                                    </Card.Body>
                                </Card>
                            </Col>
                        )}
                    </Row>

                </div>

                <Button variant="primary" type="submit">
                    Отправить
                </Button>
            </Form>
        </Container>
    );
};

export default PrimaryForm;
