import React from 'react';
import { Row, Col, Card, Container } from 'react-bootstrap';
import ModuleField from './ModuleField';

const ModulesGroup = ({ modules, onModuleChange, onAddModule, onRemoveModule }) => {
    // Добавление модуля для определенного типа
    const handleAddModuleForType = (type) => {
        onAddModule({ name: '', hours: '', type });
    };

    const renderAddButton = (typeLabel, type) => (
        <Col xs={12} md={4}>
            <h5>{typeLabel}</h5>
            <Card
                className="d-flex align-items-center justify-content-center"
                style={{
                    width: '100%',
                    minHeight: '125px',
                    border: '2px dashed #ccc',
                    cursor: 'pointer',
                    marginBottom: '10px',
                }}
                onClick={() => handleAddModuleForType(type)}
            >
                <Card.Body className="d-flex align-items-center justify-content-center">
                    <h5>+ Добавить модуль</h5>
                </Card.Body>
            </Card>
        </Col>
    );

    return (
        <Container>
            <h3>Модули программы</h3>
            <Row className="mb-4">
                {modules.map((module, index) => (
                    <ModuleField
                        key={index}
                        index={index + 1}
                        moduleData={module}
                        onChange={(data) =>
                            onModuleChange(modules.findIndex((m) => m === module), data)
                        }
                        onRemove={() =>
                            onRemoveModule(modules.findIndex((m) => m === module))
                        }
                    />
                ))}
            </Row>
            <Row>
                {renderAddButton('Обязательные модули', 'core')}
                {renderAddButton('Факультативные модули', 'optional')}
                {renderAddButton('Дополнительные модули', 'additional')}
            </Row>
        </Container>
    );
};

export default ModulesGroup;
