import React, { useState } from 'react';
import { Form, Button, Col, Row } from 'react-bootstrap';

// Маленькая форма для преподавателя
const TeacherForm = ({ onChange }) => {
  const [teacherData, setTeacherData] = useState({
    position: '',
    full_name: '',
    workExperience: '',
    workplace: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTeacherData((prevData) => {
      const updatedData = { ...prevData, [name]: value };
      onChange(updatedData);
      return updatedData;
    });
  };

  return (
    <div>
      <h3>Преподаватель</h3>
      <Form>
        <Form.Group as={Row} controlId="formTeacherPosition">
          <Form.Label column sm={2}>Должность:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="text"
              name="position"
              value={teacherData.position}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formTeacherFullName">
          <Form.Label column sm={2}>ФИО:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="text"
              name="full_name"
              value={teacherData.full_name}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formTeacherWorkExperience">
          <Form.Label column sm={2}>Опыт работы:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="text"
              name="workExperience"
              value={teacherData.workExperience}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formTeacherWorkplace">
          <Form.Label column sm={2}>Место работы:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="text"
              name="workplace"
              value={teacherData.workplace}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>
      </Form>
    </div>
  );
};

// Маленькая форма для координатора программы
const ProgramCoordinatorForm = ({ onChange }) => {
  const [coordinatorData, setCoordinatorData] = useState({
    phone: '',
    eMail: '',
    full_name: '',
    address: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCoordinatorData((prevData) => {
      const updatedData = { ...prevData, [name]: value };
      onChange(updatedData);
      return updatedData;
    });
  };

  return (
    <div>
      <h3>Координатор программы</h3>
      <Form>
        <Form.Group as={Row} controlId="formCoordinatorPhone">
          <Form.Label column sm={2}>Телефон:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="text"
              name="phone"
              value={coordinatorData.phone}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formCoordinatorEmail">
          <Form.Label column sm={2}>E-mail:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="email"
              name="eMail"
              value={coordinatorData.eMail}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formCoordinatorFullName">
          <Form.Label column sm={2}>ФИО:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="text"
              name="full_name"
              value={coordinatorData.full_name}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formCoordinatorAddress">
          <Form.Label column sm={2}>Адрес:</Form.Label>
          <Col sm={10}>
            <Form.Control
              type="text"
              name="address"
              value={coordinatorData.address}
              onChange={handleChange}
            />
          </Col>
        </Form.Group>
      </Form>
    </div>
  );
};

// Основная форма
const PrimaryForm = () => {
  const [formData, setFormData] = useState({
    ksuDepartmentID: '',
    typeGraduationDocID: '',
    programCoordinatorID: '',
    programTypeID: '',
    teacherID: '',
    accountID: '', // Это будет ID текущего пользователя
    studyPeriod: '',
    lessonSchedule: '',
    programName: '',
    programDescriptionShort: '',
    programDescription: '',
    targetAudience: '',
    programHours: '',
    educationCost: '',
  });

  const [teacherData, setTeacherData] = useState({});
  const [coordinatorData, setCoordinatorData] = useState({});

  const currentUser = {
    id: 1, // Пример ID текущего пользователя
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleTeacherChange = (teacherData) => {
    setTeacherData(teacherData);
  };

  const handleCoordinatorChange = (coordinatorData) => {
    setCoordinatorData(coordinatorData);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Объединяем все данные в один объект перед отправкой
    const dataToSend = {
      ...formData,
      teacherData,
      coordinatorData,
      accountID: currentUser.id, // Привязываем текущий аккаунт
    };

    console.log('Отправленные данные:', dataToSend);

    // Здесь вы можете отправить данные на сервер
    // axios.post('/api/primary-form', dataToSend)
    //   .then(response => console.log('Форма успешно отправлена:', response.data))
    //   .catch(error => console.error('Ошибка при отправке формы:', error));
  };

  return (
    <Form onSubmit={handleSubmit}>
      <h2>Создание Первичной Формы</h2>

      <Form.Group as={Row} controlId="formDepartment">
        <Form.Label column sm={2}>Кафедра:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="text"
            name="ksuDepartmentID"
            value={formData.ksuDepartmentID}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formGraduationDoc">
        <Form.Label column sm={2}>Тип диплома:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="text"
            name="typeGraduationDocID"
            value={formData.typeGraduationDocID}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formProgramType">
        <Form.Label column sm={2}>Тип программы:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="text"
            name="programTypeID"
            value={formData.programTypeID}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formStudyPeriod">
        <Form.Label column sm={2}>Срок обучения:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="text"
            name="studyPeriod"
            value={formData.studyPeriod}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formLessonSchedule">
        <Form.Label column sm={2}>Режим занятий:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="text"
            name="lessonSchedule"
            value={formData.lessonSchedule}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formProgramName">
        <Form.Label column sm={2}>Наименование программы:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="text"
            name="programName"
            value={formData.programName}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formProgramDescriptionShort">
        <Form.Label column sm={2}>Короткое описание программы:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="text"
            name="programDescriptionShort"
            value={formData.programDescriptionShort}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formProgramDescription">
        <Form.Label column sm={2}>Описание программы:</Form.Label>
        <Col sm={10}>
          <Form.Control
            as="textarea"
            rows={3}
            name="programDescription"
            value={formData.programDescription}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formTargetAudience">
        <Form.Label column sm={2}>Целевая аудитория:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="text"
            name="targetAudience"
            value={formData.targetAudience}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formProgramHours">
        <Form.Label column sm={2}>Общее количество часов:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="number"
            name="programHours"
            value={formData.programHours}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      <Form.Group as={Row} controlId="formEducationCost">
        <Form.Label column sm={2}>Стоимость обучения:</Form.Label>
        <Col sm={10}>
          <Form.Control
            type="number"
            name="educationCost"
            value={formData.educationCost}
            onChange={handleFormChange}
          />
        </Col>
      </Form.Group>

      {/* Вставляем компоненты для преподавателя и координатора */}
      <TeacherForm onChange={handleTeacherChange} />
      <ProgramCoordinatorForm onChange={handleCoordinatorChange} />

      <Button variant="primary" type="submit">
        Отправить
      </Button>
    </Form>
  );
};

export default PrimaryForm;
