import React from 'react';
import { Form, Card, FloatingLabel, Col, Container } from 'react-bootstrap';

// Маленькая форма для координатора
const CoordinatorForm = ({ onChange, coordinator }) => {
    const handleChange = (e) => {
        const { name, value } = e.target;
        const updatedData = { ...coordinator, [name]: value };
        onChange(updatedData); 
    };
    

    return (
        <Container>
            <Card as={Col} sm={3} style={{ width: '100%', minHeight: '300px', marginBottom: '10px', position: 'relative' }}>
                <Card.Body>
                    <Form>
                        <FloatingLabel
                            controlId="formTeacherFirstName"
                            label="Фамилия"
                            className="mb-2">
                            <Form.Control
                                type="text"
                                name="name"
                                value={coordinator.name}
                                onChange={handleChange} />
                        </FloatingLabel>

                        <FloatingLabel
                            controlId="formTeacherMiddleName"
                            label="Имя"
                            className="mb-2">
                            <Form.Control
                                type="text"
                                name="phone"
                                value={coordinator.phone}
                                onChange={handleChange} />
                        </FloatingLabel>

                        <FloatingLabel
                            controlId="formTeacherLastName"
                            label="Отчество"
                            className="mb-2">
                            <Form.Control
                                type="text"
                                name="address"
                                value={coordinator.address}
                                onChange={handleChange} />
                        </FloatingLabel>

                        <FloatingLabel
                            controlId="formTeacherPosition"
                            label="Должность"
                            className="mb-2">
                            <Form.Control
                                type="text"
                                name="email"
                                value={coordinator.email}
                                onChange={handleChange} />
                        </FloatingLabel>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
};

export default CoordinatorForm;
